INSERT INTO `brm2_3ncu3db`.`enc_pregunta` (`id`, `titulo`, `fecha`, `idCategoria`, `idPreguntaHistorial`, `idAdmin`) VALUES (3, 'Las investigaciones presentadas están acompañadas por un plan de acción viable', '2016-05-05 10:49:00', 1, 3, 1);
INSERT INTO `brm2_3ncu3db`.`enc_pregunta` (`id`, `titulo`, `fecha`, `idCategoria`, `idPreguntaHistorial`, `idAdmin`) VALUES (4, 'Analiza e interpreta los datos para identificar las oportunidades de la marca, teniendo en cuenta el escenario digital vs las oportunidades de su negocio en el mundo físico.', '2016-05-05 10:49:00', 1, 4, 1);
INSERT INTO `brm2_3ncu3db`.`enc_pregunta` (`id`, `titulo`, `fecha`, `idCategoria`, `idPreguntaHistorial`, `idAdmin`) VALUES (5, 'Hay una comunicación clara y fluida de objetivos y alcance de cada proyecto.', '2016-05-05 10:49:00', 2, 5, 1);
INSERT INTO `brm2_3ncu3db`.`enc_pregunta` (`id`, `titulo`, `fecha`, `idCategoria`, `idPreguntaHistorial`, `idAdmin`) VALUES (6, 'La administración de la cuenta y las propuestas están alineadas con las reuniones y actas generadas en las mismas. ', '2016-05-05 10:49:00', 2, 6, 1);
