INSERT INTO `brm2_3ncu3db`.`enc_pregunta` (`id`, `titulo`, `fecha`, `idCategoria`, `idPreguntaHistorial`, `idAdmin`) VALUES (10, 'Demuestra conocimiento de la marca a través de análisis enmarcados dentro del contexto económico, entendiendo los factores que influyen directos o indirectos en su negocio, proyectando  consecuencias en el tiempo', '2016-05-05 10:49:00', 3, 10, 1);
